package de.demowebshop.tests;import de.demowebshop.core.TestBase;import org.testng.Assert;import org.testng.annotations.AfterMethod;import org.testng.annotations.BeforeMethod;import org.testng.annotations.Test;public class ItemTests extends TestBase {    @BeforeMethod    public void precondition() {        app.getUser().clickOnLoginLink();        app.getUser().fillLoginForm("demiyncevaanna@gmail.com", "Anna14031981$");        app.getUser().clickOnLoginButton();    }    @Test    public void addItemToCartTest() {        String name = app.getItem().getItemName("3");        app.getItem().clickOnItemInList("3");        app.getItem().clickOnShoppingCartLink();        Assert.assertTrue(app.getContact().verifyByName(name));    }    @AfterMethod    public void postCondition() {        app.getItem().removeItemFromCart();    }}